    private void objetoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_objetoActionPerformed

    String cod=codigo.getText();
    String nom=nombre.getText();
    String tipseguro=seguro.getSelectedItem().toString();
    double sueldo=Double.parseDouble(sueldobase.getText());
    double extras=Double.parseDouble(horasextras.getText());
    String area=Area.getSelectedItem().toString();
    e=new empleado(cod, nom, area, sueldo, extras, tipseguro);
           
    //public empleado(String codigo,String nombre, String area, double sueldobase,double horasextras, String seguro){
    
    
    
    
    
    }//GEN-LAST:event_objetoActionPerformed

