    private void mostrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_mostrarActionPerformed
    
    textogrande.append("Código            :"+e.codigo+"\n");  
    textogrande.append("Nombre            :"+e.nombre+"\n"); 
    textogrande.append("Seguro            :"+e.tipo_seg+"\n"); 
    textogrande.append("Sueldo base       :"+e.sueldobase+"\n"); 
    textogrande.append("Horas extras      :"+e.horasextras+"\n");
    textogrande.append("Área              :"+e.area+"\n"); 
    textogrande.append("Monto Extra       :"+e.montoextra()+"\n"); 
    textogrande.append("Monto Essalud     :"+e.montoessalud()+"\n"); 
    textogrande.append("Monto seguro      :"+e.montoseguro()+"\n"); 
    textogrande.append("Monto de descuento:"+e.montodescuento()+"\n"); 
    textogrande.append("Sueldo bruto      :"+e.montobruto()+"\n"); 
    textogrande.append("Sueldo neta       :"+e.sueldoneto()+"\n"); 
        
        
    }//GEN-LAST:event_mostrarActionPerformed

